import axios from "axios"
export const placeOrder = (token , subtotal) =>(dispatch, getState) => {


    dispatch({type:'PLACE_ORDER-REQUEST'})
    const currentUser = getState().loginUserReducer.currentUser
    const cartItems = getState().cartReducer.cartItems

    try {
        dispatch({type: 'PLACE_ORDER-SUCCESS' })
        const response = axios.post('/api/orders/placeOrder' , {token , subtotal , currentUser, cartItems})
        console.log(response)

    } catch (error) {

        dispatch({ type: 'PLACE_ORDER-FAILED' })

        console.log(error)


    }

}
