    const[mediumprice , setmediumprice] = useState()
    const[largeprice , setlargeprice] = useState()
    const[image , setimage] = useState('')
    const[description , setdescription] = useState("")
    
    return (
        <div>
            <div>
                <h1>Add Pizza</h1>

                <Form>
                    <input type='text' placeholder="name" value={name} onChange={(e)=>{setname(e.target.value)}}/>
                    <input type='text' placeholder="small varient price" value={smallprice} onChange={(e)=>{setsmallprice(e.target.value)}}/>
                    <input type='text' placeholder="" value={name} onChange={(e)=>{setname(e.target.value)}}/>mediumprice
                </Form>

            </div>
        </div>
    )
}
