export const placeOrderReducer =(state={} , action) => {

    switch (action.type)
    {
        case 'PLACE_ORDER-REQUEST': return {
            loading:true
        }
        case 'PLACE_ORDER-SUCCESS': return {
            loading:false,
            success:true
        }
        case 'PLACE_ORDER-FAILED': return {
            loading:false,
            error:action.payload
        }
        default : return state
    }
}



export const getUserOrderReducer = (state = {pizzas : []}, action) => {

    switch (action.type) 
    {
        case 'GET_PIZZAS_REQUEST': return{
            loading : true,
            ...state
        }  
        case 'GET_PIZZAS_SUCCESS': return{
            loading : false,
            pizzas: action.payload
        }  
        case 'GET_PIZZAS_FAILED': return{
            error: action.payload,
            loading : false

        }  
        default: return state
    }

}
