import React, { useEffect } from 'react';
import { Route, Switch } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux';
import AddPizza from './AddPizza';
import OrdersList from './OrdersList';
import PizzasList from './PizzasList';
import UsersList from './UsersList';

export default function AdminScreen() {
    const userstate = useSelector((state) => state.loginUserReducer)
    const { currentUser } = userstate;
    const dispatch = useDispatch()

    useEffect(() => {

        if (!currentUser.isAdmin) {
            window.location.href = '/'
        }
    }, [])

    return (
        <div>
            <div className='row justify-content-center'>
                <div className="col-md-10">
                    <h2 style={{ fontSize: "28px" }}>Admin Screen</h2>
                    <ul className="adminfunctions">
                        <li>
                            <a href='/admin/userlist'>Users List</a>
                            <a href='/admin/pizzaslist'>Pizzas List</a>
                            <a href='/admin/'>Add New Pizza</a>
                            <a href=''>Orders List</a>
                        </li>
                    </ul>

                    <Switch>
                        <Route path='/admin/userslist' component={UsersList} exact/>
                        <Route path='/admin/pizzaslist' component={PizzasList} exact/>
                        <Route path='/admin/addpizza' component={AddPizza} exact/>
                        <Route path='/admin/orderlist' component={OrdersList} exact/>
                    </Switch>

                </div>
            </div>
        </div>
    )
}
