import React, { useState } from 'react'

export default function AddPizza() {
    const[name , setname] = useState("")
    const[smallprice , setsmallprice] = useState()
    const[mediumprice , setmediumprice] = useState()
    const[largeprice , setlargeprice] = useState()
    const[image , setimage] = useState('')
    const[description , setdescription] = useState("")
    const[category , setcategory] = useState("")
    
    return (
        <div>
            <div>
                <h1>Add Pizza</h1>

                <form>
                    <input type='text' placeholder="name" value={name} onChange={(e)=>{setname(e.target.value)}}/>
                    <input type='text' placeholder="small varient price" value={smallprice} onChange={(e)=>{setsmallprice(e.target.value)}}/>
                    <input type='text' placeholder="" value={mediumprice} onChange={(e)=>{setmediumprice(e.target.value)}}/>
                    <input type='text' placeholder="" value={largeprice} onChange={(e)=>{setlargeprice(e.target.value)}}/>
                    <input type='text' placeholder="" value={image} onChange={(e)=>{setimage(e.target.value)}}/>
    const[description , setdescription] = useState("")
                    <input type='text' placeholder="" value={description} onChange={(e)=>{setdescription(e.target.value)}}/>

                </form>

            </div>
        </div>
    )
}
